class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        import copy
        rows = [[board[i][j] for j in range(n)] for i in range(m)]
        for i in range(m):
            for j in range(n):
                rows[i][j] = (rows[i][j], j)
            rows[i].sort(reverse=True)
        ans = float('-inf')
        d = list()
        for a in range(m):
            x = rows[a][:3]
            # print(x)
            for b in range(a + 1, m):
                y = rows[b][:3]
                for u in x:
                    for i in y:
                        if i[1] != u[1]:
                            d.append((u[0] + i[0], a, b, u[1], i[1]))
        d.sort(reverse=True)
        # print(d)
        for i in range(m):
            for j in range(n):
                v = board[i][j]
                for x in d:
                    if i != x[1] and i != x[2] and j != x[3] and j != x[4]:
                        ans = max(ans, v + x[0])
                        break
        return ans
