class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        ans = -inf
        sb = []
        for r in board:
            sr = sorted([(n, j) for j, n in enumerate(r)], reverse=True)
            tmp, i = sr[:3], 3
            while i < len(sr) and i < 5 and sr[i][0] == tmp[-1][0]:
                tmp.append(sr[i])
                i += 1
            sb.append(tmp)
        for i in range(len(sb)):
            for j in range(i + 1, len(sb)):
                for k in range(j + 1, len(sb)):
                    if ans >= sb[i][0][0] + sb[j][0][0] + sb[k][0][0]:
                        continue
                    for a, b, c in product(sb[i], sb[j], sb[k]):
                        if a[1] != b[1] and a[1] != c[1] and b[1] != c[1]:
                            ans = max(ans, a[0] + b[0] + c[0])
        return ans
