class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        f = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                f.append([board[i][j], i, j])
        f.sort()
        f = f[::-1]
        ans = [-int(1e10)]
        fi = []
        fj = []
        a = []
        def find(index, deep):
            if deep == 3:
                ans[0] = max(ans[0], sum(a))
                return


            if deep == 0:
                for i in range(index, len(f) - 2 + deep):
                    if f[i][0] + f[i + 1][0] + f[i + 2][0] < ans[0]:
                        break
                    if (f[i][1] not in fi) and (f[i][2] not in fj):
                        fi.append(f[i][1])
                        fj.append(f[i][2])
                        a.append(f[i][0])
                        find(index + 1, deep + 1)
                        fi.pop()
                        fj.pop()
                        a.pop()
            elif deep == 1:
                for i in range(index, len(f) - 2 + deep):
                    if f[i][0] + f[i + 1][0] + a[0] < ans[0]:
                        break
                    if (f[i][1] not in fi) and (f[i][2] not in fj):
                        fi.append(f[i][1])
                        fj.append(f[i][2])
                        a.append(f[i][0])
                        find(index + 1, deep + 1)
                        fi.pop()
                        fj.pop()
                        a.pop()
            elif deep == 2:
                for i in range(index, len(f) - 2 + deep):
                    if f[i][0] + a[1] + a[0] < ans[0]:
                        break
                    if (f[i][1] not in fi) and (f[i][2] not in fj):
                        fi.append(f[i][1])
                        fj.append(f[i][2])
                        a.append(f[i][0])
                        find(index + 1, deep + 1)
                        fi.pop()
                        fj.pop()
                        a.pop()


        #print(f)
        find(0, 0)
        return ans[0]
