class Solution {
public:
    typedef long long int ll;
    ll maxSum(int r1, int r2, int r3, vector<vector<pair<int, int>>> &vec) {
        pair<int, int> p1, p2, p3;
        ll a, b, c, a1, b1, c1;
        p1 = vec[r1][0];
        if(vec[r2][0].second == p1.second) {
            p2 = vec[r2][1];
        } else {
            p2 = vec[r2][0];
        }

        if(vec[r3][0].second == p1.second || vec[r3][0].second == p2.second) {
                if(vec[r3][1].second == p1.second || vec[r3][1].second == p2.second) {
                    p3 = vec[r3][2];
                } else {
                    p3 = vec[r3][1];
                }
            } else {
                p3 = vec[r3][0];
            }

        a = (ll)p1.first, b = (ll)p2.first, c = (ll)p3.first;

        if(vec[r3][0].second == p1.second) {
            p3 = vec[r3][1];
        } else {
            p3 = vec[r3][0];
        }

        if(vec[r2][0].second == p1.second || vec[r2][0].second == p3.second) {
                if(vec[r2][1].second == p1.second || vec[r2][1].second == p3.second) {
                    p2 = vec[r2][2];
                } else {
                    p2 = vec[r2][1];
                }
            } else {
                p2 = vec[r2][0];
            }

        a1 = (ll)p1.first, b1 = (ll)p2.first, c1 = (ll)p3.first;

        ll res1 = a + b + c, res2 = a1 + b1 + c1;

        return max(res1, res2);


    }
    long long maximumValueSum(vector<vector<int>>& a) {

        int n = a.size(), m = a[0].size();
        vector<vector<pair<int, int>>> vec;

        for(int i = 0; i < n; i++) {
            vector<pair<int, int>> v;
            for(int j = 0; j < m; j++) {
                v.push_back({a[i][j], j});
            }
            sort(v.rbegin(), v.rend());
            vec.push_back(v);
        }

        ll ans = -1e12;

        for(int i = 0; i < n; i++) {
            for(int j = i + 1; j < n; j++) {
                for(int k = j + 1; k < n; k++) {
                    ll p, q, r;
                    p = maxSum(i, j, k, vec);
                    q = maxSum(j, i, k, vec);
                    r = maxSum(k, i, j, vec);
                    ans = max(ans, max(p, max(q, r)));
                }
            }
        }

        return ans;




    }
};
