#define ll long long
#define ff first
#define ss second

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        vector<pair<ll,pair<int,int>>>v;
        int n=b.size(),m=b[0].size();
        for(int i=0;i<n;i++){
            vector<pair<ll,pair<int,int>>>d;
            for(int j=0;j<m;j++){
                d.push_back({b[i][j],{i,j}});
            }
            sort(d.begin(),d.end());
            reverse(d.begin(),d.end());
            v.push_back(d[1]);v.push_back(d[2]);
            v.push_back(d[0]);
        }
        sort(v.begin(),v.end());
        ll ans=INT64_MIN;
        int a=v.size();
        map<int,int>q;
        vector<pair<ll,pair<int,int>>>l;
        for(int i=a-1;i>=0;i--){
            if(q[v[i].ss.ss]<3){
                q[v[i].ss.ss]++;
                l.push_back(v[i]);
            }
        }
        int e=l.size();
        for(int i=0;i<15;i++){
            for(int j=i+1;j<e;j++){
                if(l[i].ss.ff==l[j].ss.ff || l[i].ss.ss==l[j].ss.ss){
                    continue;
                }
                for(int k=j+1;k<e;k++){
                    if(l[i].ss.ff!=l[j].ss.ff && l[i].ss.ff!=l[k].ss.ff && l[k].ss.ff!=l[j].ss.ff && l[i].ss.ss!=l[j].ss.ss && l[i].ss.ss!=l[k].ss.ss && l[k].ss.ss!=l[j].ss.ss ){
                        // cout<<v[i].ff<<" "<<v[j].ff<<" "<<v[k].ff<<endl;
                        ans=max(ans,l[i].ff+l[j].ff+l[k].ff);
                    }
                }
            }
        }
        return ans;
    }
};
