class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        nrow = len(board)
        ncol = len(board[0])

        top3_arr = [[] for _ in range(ncol)]
        for i in range(nrow):
            tmp = sorted([(v, i, j) for j, v in enumerate(board[i])])[-3:]
            for v, ii, jj in tmp:
                top3_arr[jj].append((v, ii, jj))

        top3_list = []
        for j in range(ncol):
            tmp = sorted(top3_arr[j])[-3:]
            top3_list.extend(tmp)

        nums = sorted(top3_list)[-15:]
        res = None
        for i in range(len(nums)):
            for j in range(len(nums)):
                for k in range(len(nums)):
                    v1, i1, j1 = nums[i]
                    v2, i2, j2 = nums[j]
                    v3, i3, j3 = nums[k]
                    if i1 == i2 or i3 == i1 or i3 == i2:
                        continue
                    if j1 == j2 or j3 == j1 or j3 == j2:
                        continue
                    tmp = v1 + v2 + v3

                    res = tmp if res is None else max(res, tmp)

        return res
