class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        pre = []
        for i in range(m):
            for j in range(n):
                pre.append([board[i][j], i, j])
        pre.sort(reverse=True)
        t = m * n

        def cal2(x1, y1, x2, y2):
            for i in range(t):
                if pre[i][1] != x1 and pre[i][1] != x2 and pre[i][2] != y1 and pre[i][2] != y2:
                    return i

        def cal1(x1, y1):
            for i in range(t):
                if pre[i][1] != x1 and pre[i][2] != y1:
                    return i

        res = -inf
        x1 = pre[0][1]
        for y1 in range(n):
            p1=board[x1][y1]
            k = cal1(x1, y1)
            y2 = pre[k][2]
            for x2 in range(m):
                if x2==x1:
                    continue
                p2= p1+board[x2][y2]
                r = pre[cal2(x1, y1, x2, y2)][0]
                p3=p2+r
                if p3>res:
                    res=p3
            x2 = pre[k][1]
            for y2 in range(n):
                if y2==y1:
                    continue
                p2= p1+board[x2][y2]
                r = pre[cal2(x1, y1, x2, y2)][0]
                p3=p2+r
                if p3>res:
                    res=p3

        y1 = pre[0][2]
        for x1 in range(m):
            p1=board[x1][y1]
            k = cal1(x1, y1)
            y2 = pre[k][2]
            for x2 in range(m):
                if x2==x1:
                    continue
                p2= p1+board[x2][y2]
                r = pre[cal2(x1, y1, x2, y2)][0]
                p3=p2+r
                if p3>res:
                    res=p3
            x2 = pre[k][1]
            for y2 in range(n):
                if y2==y1:
                    continue
                p2= p1+board[x2][y2]
                r = pre[cal2(x1, y1, x2, y2)][0]
                p3=p2+r
                if p3>res:
                    res=p3
        return res
