class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        hp = [[] for _ in range(m)]
        for i in range(m):
            for j in range(n):
                a = board[i][j]
                if len(hp[i]) < 3:
                    heappush(hp[i], (a, i, j))
                elif hp[i][0][0] < a:
                    heappop(hp[i])
                    heappush(hp[i], (a, i, j))
        A = []
        for h in hp:
            A += h
        A.sort(reverse=True)

        ans = -inf
        for i in range(m):
            for j in range(i + 1, m):
                for a, _, x in hp[i]:
                    for b, _, y in hp[j]:
                        if y == x: continue
                        cur = a + b
                        for c, zx, zy in A:
                            if zx in (i, j) or zy in (x, y): continue
                            cur += c
                            if cur > ans: ans = cur
                            break
        return ans
