class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int rz = b.size();
        int cz = b[0].size();
        for(int i=0;i<rz;i++)
            for(int j=0;j<cz;j++) m[b[i][j]].push_back({i,j});
        return compute(b,0);
    }
    long long compute(vector<vector<int>>& b, int d)
    {
        //cout<<"I: "<<d<<endl;
        //for(auto e : used) cout<<e.first<<" "<<e.second<<"  ";
        //cout<<endl;
        if(d==3) return 0;
        long long temp = -100000000000000LL;
        vector<pair<int,int>> p0;
        for(auto it = m.rbegin();it!=m.rend() && p0.size()<4;it++)
        {
            for(auto e : it->second)
            {
                bool isFound = false;
                for(int j=0;j<used.size();j++) if(used[j].first==e.first || used[j].second==e.second) isFound= true;
                if(isFound) continue;
                int r = getSameRow(p0,e.first);
                int c = getSameCol(p0,e.second);
                if(r<=1 && c<=1)
                {
                    p0.push_back(e);
                    used.push_back(e);
                    temp = max(b[e.first][e.second]+compute(b,d+1),temp);
                    used.pop_back();
                }
            }
        }
        return temp;
    }
    int getSameRow(vector<pair<int,int>>& p0, int x)
    {
        int r = 0;
        for(auto e : p0) if(e.first==x) r++;
        return r;
    }
    int getSameCol(vector<pair<int,int>>& p0, int x)
    {
        int r = 0;
        for(auto e : p0) if(e.second==x) r++;
        return r;
    }
    vector<pair<int,int>> used;
    map<int,vector<pair<int,int>>> m;
};
