class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        from sortedcontainers import SortedList

        bason = SortedList(key = lambda x: -x[2])
        ans = -10**45
        for ea in range(len(board)):
            for jk in range(len(board[0])):
                bason.add((ea, jk, board[ea][jk]))
        for i in range(len(bason)):
            for j in range(i + 1, len(bason)):
                if bason[i][2] + bason[j][2] + bason[j][2] < ans:
                    break
                if bason[i][0] == bason[j][0] or bason[i][1] == bason[j][1]:
                    continue
                hits = set([bason[i][0], bason[j][0]])
                bits = set([bason[i][1], bason[j][1]])
                for k in range(j + 1, len(bason)):

                    if bason[i][2] + bason[j][2] + bason[k][2] < ans:
                        break
                    if bason[k][0] in hits:
                        continue
                    if bason[k][1] in bits:
                        continue
                    ans = max(ans, bason[i][2] + bason[j][2] + bason[k][2])
                    break



        return ans
