class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long n = board.size(), m = board[0].size();
        vector<pair<long long, long long>> tot;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                tot.emplace_back(make_pair(board[i][j], i * m + j));
            }
        }
        long long sz = n * m;
        sort(tot.begin(), tot.end());
        long long ans = -3e9;
        vector<long long> cntl(n + 10, 0), cntr(m + 10, 0);
        vector<pair<long long, long long>> newtot;
        int tmp = 0;
        for (int i = sz - 1; i >= 0; i--) {
            int x = tot[i].second / m, y = tot[i].second % m;
            if (cntl[x] <= 3 && cntr[y] <= 3) {
                newtot.emplace_back(tot[i]);
                if (cntl[x] == 0) tmp++;
                if (cntr[y] == 0) tmp++;
                cntl[x]++;
                cntr[y]++;
            }
            if (tmp >= 300) break;
        }
        sz = newtot.size();
        for (int i = 0; i < sz; i++) {
            for (int j = i + 1; j < sz; j++) {
                for (int k = j + 1; k < sz; k++) {
                    long long x1 = newtot[i].second / m, y1 = newtot[i].second % m;
                    long long x2 = newtot[j].second / m, y2 = newtot[j].second % m;
                    long long x3 = newtot[k].second / m, y3 = newtot[k].second % m;
                    if (x1 != x2 && x2 != x3 && x1 != x3 && y1 != y2 && y2 != y3 && y1 != y3) {
                        ans = max(ans, newtot[i].first + newtot[j].first + newtot[k].first);
                    }
                }
            }
        }
        return ans;
    }
};
