class Solution {
public:
    long long maximumValueSum(vector<vector<int>>&v)
    {
        int n=v.size(),m=v[0].size();
        long long ans=-1e10;
        vector<long long>mx(n,-1e10),smx(n,-1e10),mxid(n,-1),smxid(n,-1),tmx(n,-1e10),tmxid(n,-1);
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(v[i][j]>mx[i])       tmx[i]=smx[i],smx[i]=mx[i],mx[i]=v[i][j],tmxid[i]=smxid[i],smxid[i]=mxid[i],mxid[i]=j;
                else if(v[i][j]>smx[i]) tmx[i]=smx[i],smx[i]=v[i][j],tmxid[i]=smxid[i],smxid[i]=j;
                else if(v[i][j]>tmx[i]) tmx[i]=v[i][j],tmxid[i]=j;
            }
        }

        for(int r1=0;r1<n-2;r1++)
        {
            long long c_mx=mx[r1+1],c_smx=smx[r1+1],c_tmx=tmx[r1+1];
            int c_mxid=mxid[r1+1],c_smxid=smxid[r1+1],c_tmxid=tmxid[r1+1];

            for(int r2=r1+2;r2<n;r2++)
            {
                vector<pair<long long,long long>>t1{{mx[r1],mxid[r1]},{smx[r1],smxid[r1]},{tmx[r1],tmxid[r1]}};
                vector<pair<long long,long long>>t{{mx[r2],mxid[r2]},{smx[r2],smxid[r2]},{tmx[r2],tmxid[r2]}};
                vector<pair<long long,long long>>col{{c_mx,c_mxid},{c_smx,c_smxid},{c_tmx,c_tmxid}};
                for(auto &x:t1)
                    for(auto &y:t)
                        for(auto &z:col)
                        {
                            long long v1=x.first,v2=y.first,v3=z.first;
                            int id1=x.second,id2=y.second,id3=z.second;
                            if(id1!=id2&&id2!=id3&&id1!=id3)            ans=max(ans,v1+v2+v3);
                        }
                for(auto &tt:t)
                {
                    long long val=tt.first,id=tt.second;
                    if(val>c_mx)        
                    {
                        if(id==c_mxid)
                        {
                            c_mx=val;
                            continue;
                        }
                        c_tmx=c_smx,c_smx=c_mx,c_mx=val,c_tmxid=c_smxid,c_smxid=c_mxid,c_mxid=id;
                    }
                    else if(val>c_smx)
                    {
                        if(id==c_mxid)  continue;
                        if(id==c_smxid)
                        {
                            c_smx=val;
                            continue;
                        }
                        c_tmx=c_smx,c_smx=val,c_tmxid=c_smxid,c_smxid=id;
                    }
                    else if(val>c_tmx)  
                    {
                        if(id==c_mxid)  continue;
                        if(id==c_smxid) continue;
                        c_tmx=val,c_tmxid=id;
                    }
                }
            }
        }
        return ans;    
    }
};
