class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<array<int,2>>> a(n);
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                a[i].push_back({board[i][j], j});
            }
            sort(a[i].rbegin(), a[i].rend());
        }
        #define ll long long
        ll ans = -1e18;

        vector<int> ord(n);
        iota(ord.begin(), ord.end(), 0);
        srand(time(NULL));
        random_shuffle(ord.begin(), ord.end());

        for(int i : ord){
            for(int j=i+1; j<n; j++){
                for(int k=j+1; k<n; k++){
                    for(int c1=0; c1<3; c1++){
                        if(((ll)a[i][c1][0]) + a[j][0][0] + a[k][0][0] <= ans) break;
                        for(int c2=0; c2<3; c2++){
                            if(a[i][c1][1] == a[j][c2][1]) continue;
                            if(((ll)a[i][c1][0]) + a[j][c2][0] + a[k][0][0] <= ans) break;
                            for(int c3=0; c3<3; c3++){
                                if(a[k][c3][1] == a[i][c1][1] || a[k][c3][1] == a[j][c2][1]) continue;
                                ans = max(ans, ((ll)a[i][c1][0]) + a[j][c2][0] + a[k][c3][0]);
                                break;
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
