#define ll long long
#define ff first
#define ss second
#define pii pair<long long,long long>
class Solution {
public:

    void getMax(int i,vector<vector<int>>& v,vector<vector<pair<ll,ll>>>&mp){
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;
        pq.push({v[i][0],0});
        pq.push({v[i][1],1});
        pq.push({v[i][2],2});
        int m=v[0].size();
        for(int j=3;j<m;j++){
            pq.push({v[i][j],j});
            pq.pop();
        }
        while(pq.size()){
            mp[i].push_back(pq.top());
            pq.pop();
        }
        return;
    }

    long long maximumValueSum(vector<vector<int>>& v) {

        int n=v.size();
        int m=v[0].size();
        ll ans=-1e12;
        vector<vector<int>> t=vector<vector<int>>(n,vector<int>(m));
        for(int j=0;j<m;j++){
            t[n-1][j]=v[n-1][j];
        }
        for(int i=n-2;i>=0;i--){
            for(int j=0;j<m;j++){
                t[i][j]=max(t[i+1][j],v[i][j]);
            }
        }
        vector<vector<pii>>mp(n+1);
        vector<vector<pii>>mt(n+1);
        for(int i=0;i<n;i++){
            getMax(i,v,mp);
        }
        for(int i=0;i<n;i++){
            getMax(i,t,mt);
        }
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n-1;j++){
                int k=j+1;
                for(int a=0;a<3;a++){
                        for(int b=0;b<3;b++){
                             for(int c=0;c<3;c++){
                                 if(mp[i][a].ss!=mp[j][b].ss and mp[i][a].ss!=mt[k][c].ss and mp[j][b].ss!=mt[k][c].ss){
                                    ans=max(ans,mp[i][a].ff+mp[j][b].ff+mt[k][c].ff);
                                }
                             }

                        }
                }
            }
        }
        return ans;

    }
};
