class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length, n = board[0].length;
        int[][] c1 = new int[n][m], c2 = new int[n][m];
        for (int j = 0; j < n; j++) {
            for (int i = 0; i < m; i++) {
                if (i == 0) c1[j][i] = board[i][j];
                else c1[j][i] = Math.max(c1[j][i - 1], board[i][j]);
            }
            for (int i = m - 1; i >= 0; i--) {
                if (i == m - 1) c2[j][i] = board[i][j];
                else c2[j][i] = Math.max(c2[j][i + 1], board[i][j]);
            }
        }

        long res = Long.MIN_VALUE;
        for (int i = 1; i < m - 1; i++) {
            List<int[]> q1 = new ArrayList<>();
            List<int[]> q2 = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                int y = c1[j][i - 1], z = c2[j][i + 1];
                q1.add(new int[]{y, j});
                q2.add(new int[]{z, j});
            }
            Collections.sort(q1, (o1, o2) -> o2[0] - o1[0]);
            Collections.sort(q2, (o1, o2) -> o2[0] - o1[0]);
            // int[] x1 = q1.get(0), x2 = q1.get(1), x3 = q1.get(2);
            // int[] y1 = q1.get(0), y2 = q1.get(1), y3 = q1.get(2);
            for (int j = 0; j < n; j++) {
                int x = board[i][j];
                for (int a = 0; a < 3; a++) {
                    for (int b = 0; b < 3; b++) {
                        if (check(j, q1.get(a)[1], q2.get(b)[1])) {
                            res = Math.max(res, (long)x + q1.get(a)[0] + q2.get(b)[0]);
                        }
                    }
                }

            }


            // if (q1.get(0)[1] != q2.get(0)[1]) res = Math.max(res, x + q1.get(0)[0] + q2.get(0)[0]);
            // else {
            //     long t1 = q1.get(0)[0] + q2.get(1)[0];
            //     long t2 = q1.get(1)[0] + q2.get(0)[0];
            //     res = Math.max(res, x + Math.max(t1, t2));
            //     // System.out.println(i + " " + j + " " + t1 + " " + t2);
            // }
            // System.out.println(i + " " + j + " " + q1.get(0)[1] + " " + q2.get(0)[1] + " " + q1.get(01)[1] + " " + q2.get(1)[1]);
        }
        return res;
    }

    private boolean check(int a, int b, int c) {
        return a != b && a != c && b != c;
    }
}
