class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& A) {
      int n = A.size();
      int m = A[0].size();
      vector<vector<pair<int64_t, int>>> B(n);
      for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) B[i].push_back({A[i][j], j});
        nth_element(B[i].begin(), B[i].begin()+3, B[i].end(), greater<>());
        B[i].erase(B[i].begin()+3, B[i].end());
      }
      set<int> S;
      for (int k = 0; k < 3; k++) {
        vector<int> C(n); for (int i = 0; i < n; i++) C[i]=i;
        sort(C.begin(), C.end(), [&](auto &l, auto &r) { return B[l][k]>B[r][k]; });
        for (int i = 0; i < 9 && i < n; i++) S.insert(C[i]);
      }
      vector<vector<pair<int64_t, int>>> BB;
      for (auto x: S) BB.push_back(B[x]);

      int64_t ans = -0x3f3f3f3f3f3f3f3fll;
      for (int i = 0; i < BB.size(); i++) {
        for (int j = i+1; j < BB.size(); j++) {
          for (int k = j+1; k < BB.size(); k++) {
            for (auto &[x, i1]: BB[i]) {
              for (auto &[y, i2]: BB[j]) {
                for (auto &[z, i3]: BB[k]) if (i1 != i2 && i2 != i3 && i3 != i1) ans = max(ans, x+y+z);
              }
            }
          }
        }
      }
      return ans;

    }
};
