class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])
        maxv = [[-1000_000_001]*3 for _ in range(m)]
        idx = [[-1]*3 for _ in range(m)]
        for i in range(m):
            for j in range(n):
                if board[i][j] > maxv[i][0]:
                    maxv[i][0], maxv[i][1], maxv[i][2] = board[i][j], maxv[i][0], maxv[i][1]
                    idx[i][0], idx[i][1], idx[i][2] = j, idx[i][0], idx[i][1]
                elif board[i][j] > maxv[i][1]:
                    maxv[i][1], maxv[i][2] = board[i][j], maxv[i][1]
                    idx[i][1], idx[i][2] = j, idx[i][1]
                elif board[i][j] > maxv[i][2]:
                    maxv[i][2] = board[i][j]
                    idx[i][2] = j

        v1 = v2 = v3 = -1000_000_001
        r1 = r2 = r3 = -1
        c1 = c2 = c3 = -1
        for i in range(m):
            if maxv[i][0] > v1:
                v1, v2, v3 = maxv[i][0], v1, v2
                r1, r2, r3 = i, r1, r2
                c1, c2, c3 = idx[i][0], c1, c2
            elif maxv[i][0] > v2:
                v2, v3 = maxv[i][0], v2
                r2, r3 = i, r2
                c2, c3 = idx[i][0], c2
            elif maxv[i][0] > v3:
                v3 = maxv[i][0]
                r3 = i
                c3 = idx[i][0]

        res = -3000_000_001
        val = v1
        row = r1
        used = c1
        for i in range(m-1):
            if i == row:
                continue
            for j in range(i+1, m):
                if j == row:
                    continue
                for a in range(3):
                    for b in range(3):
                        if idx[i][a] != idx[j][b] and idx[i][a] != used and idx[j][b] != used:
                            res = max(res, maxv[i][a] + maxv[j][b] + val)
        val = v2
        row = r2
        used = c2
        for i in range(m-1):
            if i == row:
                continue
            for j in range(i+1, m):
                if j == row:
                    continue
                for a in range(3):
                    for b in range(3):
                        if idx[i][a] != idx[j][b] and idx[i][a] != used and idx[j][b] != used:
                            res = max(res, maxv[i][a] + maxv[j][b] + val)
        val = v3
        row = r3
        used = c3
        for i in range(m-1):
            if i == row:
                continue
            for j in range(i+1, m):
                if j == row:
                    continue
                for a in range(3):
                    for b in range(3):
                        if idx[i][a] != idx[j][b] and idx[i][a] != used and idx[j][b] != used:
                            res = max(res, maxv[i][a] + maxv[j][b] + val)
        return res
