class Solution {
public long maximumValueSum(int[][] board) {
    var m = board.length;
    var n = board[0].length;
    var arrLength = m * n;
    var index = 0;
    var arr = new int[m * n][3];
    for (int i = 0; i < m; i++) {
      for (int j = 0; j < n; j++) {
        arr[index][0] = board[i][j];
        arr[index][1] = i;
        arr[index][2] = j;
        index++;
      }
    }
    Arrays.sort(arr, Comparator.comparingInt(a -> -a[0]));
    long result = -10_000_000_000L;
    for (int i = 0; i < arrLength; i++) {
      var first = arr[i];
      var maxPosible = (long) first[0] * 3;
      if (maxPosible <= result)
        break;
      int[] second = new int[3];
      int[] third = new int[3];
      int j = i;
      while (true) {
        j++;
        for (; j < arrLength; j++) {
          second = arr[j];
          if (second[1] != first[1] && second[2] != first[2])
            break;
        }
        if (j == arrLength)
          break;
        long temp = first[0];
        temp += second[0];
        if (temp + second[0] <= result)
          break;
        var found = false;
        for (int k = j + 1; k < arrLength; k++) {
          third = arr[k];
          if (third[1] != first[1] && third[2] != first[2] && third[1] != second[1] && third[2] != second[2]) {
            found = true;
            break;
          }
          if (temp + third[0] <= result)
            break;
        }
        if (found) {
          long current = (long)first[0] + second[0] + third[0];
          result = Math.max(result, current);
        }
      }
    }
    return result;
  }
}
