class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        vector<vector<pair<long long,int>>> v(board.size(),vector<pair<long long,int>>(board[0].size()));
        for(int i=0;i<board.size();++i){
            for(int j=0;j<board[0].size();++j){
                v[i][j]={board[i][j],j};
            }
        }
        for(int i=0;i<board.size();++i){
            sort(v[i].begin(),v[i].end(),greater());
        }
        vector<vector<pair<long long,pair<int,int>>>> vv(board.size());
        for(int i=1;i<board.size()-1;++i){
            for(int j=i+1;j<board.size();++j){
                for(int a=0;(a<3);++a){
                    for(int b=0;(b<3);++b){
                         if(v[i][a].second!=v[j][b].second){
                             vv[i].push_back({v[i][a].first+v[j][b].first,{v[i][a].second,v[j][b].second}});
                         }
                    }
                }
            }
        }
        for(int i=0;i<board.size();++i){
            sort(vv[i].begin(),vv[i].end(),greater());
        }
        for(int i=0;i<board.size()-2;++i){
            for(int j=i+1;j<board.size()-1;++j){
                for(int a=0;a<3;++a){
                    for(int k=0;k<vv[j].size();++k){
                        if((vv[j][k].second.first!=v[i][a].second)&&(vv[j][k].second.second!=v[i][a].second)){
                            ans=max(ans,vv[j][k].first+v[i][a].first);
                            break;
                        }
                    }
                }
            }
        }
        return ans;
    }
};
