#define forz(i,a,b) for(ll i = a;i<b;i++)
typedef long long ll;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<pair<ll,ll>> rowmax1(n),rowmax2(n),rowmax3(n);

        forz(i,0,n){
            vector<pair<ll,ll>> temp;
            forz(j,0,m){
                temp.push_back({grid[i][j],j});
            }
            sort(temp.rbegin(),temp.rend());
            rowmax1[i] = temp[0];
            rowmax2[i] = temp[1];
            rowmax3[i] = temp[2];
        }

        long long ans = -1e10;

        forz(row1,0,n){
            long long tempAns1 = -1e10;
            int col1 = rowmax1[row1].second;
            int temp1 = rowmax1[row1].first;

            forz(row2,0,n){
                long long tempAns2 = -1e10;
                if (row1 == row2){
                    continue;
                }
                int col2 = -1;
                long long temp2 = -1;

                if (rowmax1[row2].second != col1){
                    col2 = rowmax1[row2].second;
                    temp2 = rowmax1[row2].first;
                }
                else{
                    col2 = rowmax2[row2].second;
                    temp2 = rowmax2[row2].first;
                }

                forz(row3,0,n){
                    if ((row3 == row1) || (row3 == row2)){
                        continue;
                    }

                    if ((rowmax1[row3].second != col1) && (rowmax1[row3].second != col2)){
                        tempAns2 = max(tempAns2, temp2 + rowmax1[row3].first);
                    } 
                    else if ((rowmax2[row3].second != col1) && (rowmax2[row3].second != col2)){
                        tempAns2 = max(tempAns2, temp2 + rowmax2[row3].first);
                    }  
                    else if ((rowmax3[row3].second != col1) && (rowmax3[row3].second != col2)){
                        tempAns2 = max(tempAns2, temp2 + rowmax3[row3].first);
                    }
                }
                tempAns1 = max(tempAns1, temp1 + tempAns2);
            }
            ans = max(ans,tempAns1);
        }

        return ans;
    }
};
