class Solution {
public:
    typedef long long ll;
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<set<pair<ll,ll> , greater<pair<int , int>>>> vp(n);

        for(int i = 0 ;i < n;i++){
            for(int j = 0 ;j < m;j++){
                vp[i].insert({board[i][j] , j});
                if(vp[i].size() > 3){
                    auto x = prev(vp[i].end());
                    vp[i].erase(x);
                }
            }
        }
        sort(vp.rbegin() , vp.rend());
        ll ans = -1e12;
        for(int i = 0 ;i < min(50 , n);i++){
            for(int j = i + 1 ;j < min(50 , n) ;j++){
                for(int k = j + 1 ;k < min(50 , n) ;k++){

                    for(auto &p : vp[i]){
                        for(auto &q: vp[j]){
                            for(auto &r : vp[k]){
                                if(p.second != q.second && p.second != r.second && q.second != r.second){
                                    ans = max(ans , p.first + q.first + r.first);
                                }
                            }
                        }
                    }

                }
            }
        }
        return ans;
    }
};
