class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])
        arr = [[[board[_][i],i] for i in range(n)] for _ in range(m)]

        def choosefrom3(r1,r2,r3):
            arr1,arr2,arr3 = sorted(arr[r1],reverse = True), sorted(arr[r2],reverse = True), sorted(arr[r3],reverse = True)
            s = set()
            for i in range(3):
                s.add(arr1[i][1])
                s.add(arr2[i][1])
                s.add(arr3[i][1])
            s = list(s)
            ll = len(s)
            maxval = -float(inf)
            maxind = [-1,-1,-1]
            #print(s,ll)
            for i in range(ll):
                for j in range(ll):
                    if j == i:
                        continue
                    for k in range(ll):
                        if k == i or k == j:
                            continue
                        if board[r1][s[i]] + board[r2][s[j]] + board[r3][s[k]] > maxval:
                            maxval = board[r1][s[i]] + board[r2][s[j]] + board[r3][s[k]]
                            maxind = [s[i],s[j],s[k]]
            return maxind, maxval

        cind, res = choosefrom3(0,1,2)
        rind = [0,1,2]
        thres = min(board[rind[0]][cind[0]], board[rind[1]][cind[1]], board[rind[2]][cind[2]])
        for rr in range(3,m):
            c1, r1 = choosefrom3(rind[0],rind[1],rr)
            c2, r2 = choosefrom3(rind[0],rind[2],rr)
            c3, r3 = choosefrom3(rind[1],rind[2],rr)
            rnew = max(r1,r2,r3)
            if rnew <= res:
                continue
            res = rnew
            if r1 == rnew:
                rind = [rind[0],rind[1],rr]
                cind = c1
            elif r2 == rnew:
                rind = [rind[0],rind[2],rr]
                cind = c2
            elif r3 == rnew:
                rind = [rind[1],rind[2],rr]
                cind = c3

        return res
