#define ll long long
const int N = 500, M = 500;
pair<int, int> maxi[N][M];
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& arr) {
        ll n = arr.size(), m = arr[0].size();
        priority_queue<array<ll,3>> pq;

        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++)
                maxi[i][j] = {arr[i][j],j};
            sort(maxi[i], maxi[i]+m, greater<pair<int,int>>());
            pq.push({maxi[i][0].first, i, maxi[i][0].second});
            pq.push({maxi[i][1].first, i, maxi[i][1].second});
            pq.push({maxi[i][2].first, i, maxi[i][2].second});
        }

        vector<array<ll, 3>> v, v2;
        ll ans = -1e10;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                while(v.size() < 6){
                    auto [x, I, J] = pq.top(); pq.pop();
                    if(j == J) v2.push_back({x,I,J});
                    else v.push_back({x,I,J});
                }

                for(int x = 0; x < 6; x++){
                    for(int y = x+1; y < 6; y++){
                        auto &[val1, I1, J1] = v[x];
                        auto &[val2, I2, J2] = v[y];

                        if(i != I1 && i != I2 && I1 != I2 &&
                           j != J1 && j != J2 && J1 != J2)
                            ans = max(ans, val1+val2+arr[i][j]);
                    }
                }

                while(!v.empty()) pq.push(v.back()), v.pop_back();
                while(!v2.empty()) pq.push(v2.back()), v2.pop_back();
            }
        }
        return ans;
    }
};
