class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        output = []
        for i,row in enumerate(board):
            cur = []
            for j,x in enumerate(row):
                cur.append((x,i,j))
            output.extend(sorted(cur)[-3:])

        def check(cur):
            returning = -10**10
            cur = sorted(cur)[::-1]
            for (v,y,x) in cur:
                new_returning =  v + max(vv for vv,yy,xx in cur if yy!=y and xx!=x)
                if new_returning <= returning:
                    break
                returning = new_returning
            return returning

        ans = -10**10
        output = sorted(output)[::-1]
        for val,y,x in output:

            cur_val = val

            cur = [(vv,yy,xx) for vv,yy,xx in output if yy!=y and xx!=x]
            ans = max(ans, cur_val + check(cur))


        return ans
