class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        vector<pair<int, pair<int, int>>>vec;
        for(int i=0; i<n; i++)
        {
            vector<pair<int, pair<int, int>>>temp;
            for(int j=0; j<m; j++)
            {
                temp.push_back({board[i][j], {i, j}});
            }
            sort(temp.begin(), temp.end());
            vec.push_back(temp[m-1]);
            vec.push_back(temp[m-2]);
            vec.push_back(temp[m-3]);
        }
        sort(vec.begin(), vec.end());
        int t=vec.size();
        long long res=-1e15;
        map<int, set<pair<int, pair<int, int>>>>hsh;
        for(int i=0; i<t; i++)
        {
            int col=vec[i].second.second;
            hsh[col].insert(vec[i]);
            if(hsh[col].size()==4)
            {
                hsh[col].erase(hsh[col].begin());
            }
        }
        vec.clear();
        for(auto &x: hsh)
        {
            for(auto &y: x.second)
            vec.push_back(y);
        }
        sort(vec.begin(), vec.end());
        reverse(vec.begin(), vec.end());
        for(int i=t-1; i>=max(0, t-101); i--)
        {
            for(int j=t-1; j>=max(0, t-101); j--)
            {
                for(int k=t-1; k>=max(0, t-101); k--)
                {
                    auto one=vec[i].second;
                    auto two=vec[j].second;
                    auto three=vec[k].second;
                    if(one.first!=two.first && one.first!=three.first && two.first!=three.first &&
                       one.second!=two.second && one.second!=three.second && two.second!=three.second)
                       {
                        res=max(res, vec[i].first+0LL+vec[j].first+0LL+vec[k].first);
                       }

                }
            }
        }
        return res;
    }
};
