using ll = long long;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        ll n=board.size(),m=board[0].size();
        vector<vector<vector<pair<ll,ll>>>> yolo(m,vector<vector<pair<ll,ll>>>(m));
        priority_queue<pair<ll,pair<ll,ll>>> allpq;
        vector<vector<pair<ll,ll>>> top3(n);
        for(int i=0;i<n;i++){
            priority_queue<pair<ll,ll>> pq;
            for(int j=0;j<m;j++) pq.push({board[i][j],j});
            for(int j=0;j<3;j++){
                top3[i].push_back(pq.top()); 
                ll p=pq.top().second;
                allpq.push({board[i][p],{i,p}});
                pq.pop();
            }
        }
        ll sz=allpq.size();
        //cout<<sz<<endl;
        for(int x=0;x<min(sz,2*m+2);x++){
            auto p=allpq.top();
            int i=p.second.second;
            for(int j=0;j<m;j++){
                if(j==i) continue;
                for(int k=j+1;k<m;k++){
                    if(k==i) continue;
                    if(yolo[j][k].size()==5) continue;
                    yolo[j][k].push_back({p.first,p.second.first});
                }
            }
            allpq.pop();
        }

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                    for(int i1=0;i1<3;i1++){
                        for(int j1=0;j1<3;j1++){
                            if(top3[i][i1].second==top3[j][j1].second) continue;
                            ll p1=top3[i][i1].second,p2=top3[j][j1].second;
                            if(p1>p2) swap(p1,p2);
                            for(int k=0;k<min(5,(int)yolo[p1][p2].size());k++){
                                if(yolo[p1][p2][k].second==i) continue;
                                if(yolo[p1][p2][k].second==j) continue;
                                ans=max(ans,top3[i][i1].first+top3[j][j1].first+yolo[p1][p2][k].first);
                            }
                        }
                    }
            }
        }
        return ans;
    }
};
