class Solution 
{
public:
    long long maximumValueSum(vector<vector<int>>& board) 
    {
        int m = board.size(), n = board[0].size();
        vector<priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<>>> max3(m);
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                max3[i].push({board[i][j], j});
                if(max3[i].size() > 3)
                {
                    max3[i].pop();
                }
            }
        }
        vector<vector<pair<long long, int>>> v3(m);
        for(int i = 0; i < m; i++)
        {
            while(!max3[i].empty())
            {
                v3[i].push_back(max3[i].top());
                max3[i].pop();
            }
        }
        vector<vector<pair<long long, int>>> m3 = v3;
        for(int i = m - 2; i >= 0; i--)
        {
            priority_queue<pair<long long, int>> pq;
            for(int a = 0; a < 3; a++)
            {
                pq.push(m3[i][a]);
                pq.push(m3[i + 1][a]);
            }
            for(int a = 0; a < 3; a++)
            {
                if(a == 0)
                {
                    m3[i][a] = pq.top();
                    pq.pop();
                }
                else if(a == 1)
                {
                    while(pq.top().second == m3[i][0].second)
                    {
                        pq.pop();
                    }
                    m3[i][a] = pq.top();
                    pq.pop();
                }
                else
                {
                    while(pq.top().second == m3[i][0].second || pq.top().second == m3[i][1].second)
                    {
                        pq.pop();
                    }
                    m3[i][a] = pq.top();
                    pq.pop();
                }
            }
        }
        long long ans = -1e9 * 3;
        for(int i = 0; i < m; i++)
        {
            for(int j = i + 1; j + 1 < m; j++)
            {
                for(int a = 0; a < 3; a++)
                {
                    for(int b = 0; b < 3; b++)
                    {
                        for(int c = 0; c < 3; c++)
                        {
                            if(v3[i][a].second != v3[j][b].second && v3[j][b].second != m3[j + 1][c].second && v3[i][a].second != m3[j + 1][c].second)
                            {
                                ans = max(ans, v3[i][a].first + v3[j][b].first + m3[j + 1][c].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
