class Solution {
public:
    bool isEqual(int a,int b,int c){
        set<int> s;
        s.insert(a);
        s.insert(b);
        s.insert(c);
        return s.size()==3;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();

        vector<vector<int>> tem;
        int rowFre[n],colFre[m];
        vector<pair<int,int>> can;
        vector<int> tem2(3);
        for(int i=0;i<n;i++) rowFre[i]=0;
        for(int i=0;i<m;i++) colFre[i]=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                tem2[0]=board[i][j];
                tem2[1]=i;
                tem2[2]=j;
                tem.push_back(tem2);
            }
        }
        sort(tem.begin(),tem.end(),greater<>());
        for(int i=0,cnt=0;i<tem.size()&&cnt<9;i++){
            if(rowFre[tem[i][1]]<3&&colFre[tem[i][2]]<3){
                can.push_back({tem[i][1],tem[i][2]});
                rowFre[tem[i][1]]++;
                colFre[tem[i][2]]++;
                cnt++;
            }
        }

        long long ans=-1e10;
        for(int i=0;i<can.size();i++){
            for(int j=0;j<can.size();j++){
                for(int k=0;k<can.size();k++){
                    if(isEqual(can[i].first,can[j].first,can[k].first)&&isEqual(can[i].second,can[j].second,can[k].second)){
                        ans=max(ans,(long long)board[can[i].first][can[i].second]+(long long)board[can[j].first][can[j].second]+(long long)board[can[k].first][can[k].second]);
                    }
                }
            }
        }
        return ans;
    }
};
