class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=LLONG_MIN;
        int n=board.size(),m=board[0].size();
        vector<vector<int>> v;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v.push_back({board[i][j],i,j});
            }
        }
        sort(v.rbegin(), v.rend());
        int x=v.size();
        for(int i=0;i<min(500,x);i++){
            for(int j=i+1;j<min(i+500,x);j++){
                for(int k=j+1;k<min(j+10,x);k++){

                    long long value1 = v[i][0], row1 = v[i][1], col1 = v[i][2];
                    long long value2 = v[j][0], row2 = v[j][1], col2 = v[j][2];
                    long long value3 = v[k][0], row3 = v[k][1], col3 = v[k][2];

                    if (row1 != row2 && row1 != row3 && row2 != row3 &&
                        col1 != col2 && col1 != col3 && col2 != col3) {
                        ans = max(ans, value1 + value2 + value3);
                    }
                }
            }
        }
        return ans;
    }
};
