struct node{
    int first;
    int second;
    node(int a, int b) : first(a), second(b){}
    bool operator<(const node& t) const {
        return first > t.first;
    }
    node(){}
};

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int m = b.size();
        int n = b[0].size();
        node v[500*500];
        int k = 0;
        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                v[k++] = node(b[i][j], i*n+j);
            }
        }
        long long ans = (long long)b[0][0] + b[1][1] + b[2][2];
        sort(v, v+k);
        //reverse(v.begin(), v.end());
        for(int i=0; i<4; i++) {
            int x1 = v[i].second/n;
            int y1 = v[i].second%n;
            int nj = 0;
            for(int j=i+1; j<m*n && nj < 4; j++) {
                int x2 = v[j].second/n;
                int y2 = v[j].second%n;
                if(x1==x2 || y1==y2) continue;
                ++nj;
                for(int k=j+1; k<m*n; k++) {
                    int x3 = v[k].second/n;
                    int y3 = v[k].second%n;
                    if(x3==x2 || x3==x1 || y3==y2 || y3==y1) continue;
                    long long s = (long long)b[x1][y1]+b[x2][y2]+b[x3][y3];
                    if(s > ans) {
                        ans = s;
                    }
                }
            }
        }
        return ans;
    }
};
