class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size();
        vector<vector<int>>dp(n,vector<int>(m,-2e9));
        vector<vector<int>>dp2(n,vector<int>(m,-2e9));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                dp[i][j]=v[i][j];
                if(i)dp[i][j]=max(dp[i][j],dp[i-1][j]);
            }
        }
        for(int i=n-1;i>=0;i--){
            for(int j=m-1;j>=0;j--){
                dp2[i][j]=v[i][j];
                if(i!=n-1)dp2[i][j]=max(dp2[i][j],dp2[i+1][j]);
            }
        }
        long long ans=-4e9;
        for(int i=1;i+1<n;i++){
            for(int j=0;j<m;j++){
                vector<pair<int,int>>a(3,make_pair(-2e9,-1));
                vector<pair<int,int>>b(3,make_pair(-2e9,-1));
                for(int k=0;k<m;k++){
                    if(k==j)continue;
                    for(int l=0;l<3;l++){
                        if(dp[i-1][k]>a[l].first){
                            for(int o=1;o>=l;o--){
                                a[o+1]=a[o];
                            }
                            a[l]=make_pair(dp[i-1][k],k);
                            break;
                        }
                    }
                    for(int l=0;l<3;l++){
                        if(dp2[i+1][k]>b[l].first){
                            for(int o=1;o>=l;o--){
                                b[o+1]=b[o];
                            }
                            b[l]=make_pair(dp2[i+1][k],k);
                            break;
                        }
                    }
                }
                for(int k=0;k<3;k++){
                    for(int l=0;l<3;l++){
                        if(a[k].second!=b[l].second)
                        ans=max(ans,1LL*a[k].first+b[l].first+v[i][j]);
                    }
                }
            }
        }
        return ans;
    }
};
