class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        long long ans = LLONG_MIN; 


        vector<vector<pair<int, pair<int, int>>>> bb(n, vector<pair<int, pair<int, int>>>(m));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                bb[i][j] = {board[i][j], {i, j}};
            }
        }


        for (int i = 0; i < n; i++) {
            sort(bb[i].begin(), bb[i].end(), greater<>());
        }


        for (int i = 0; i < n; i++) {
            for (int x = 0; x < 3; x++) { 
                auto a = bb[i][x];
                for (int j = i+1; j < n; j++) {

                    for (int y = 0; y < 3; y++) {
                        auto b = bb[j][y];

                        if (b.second.second == a.second.second) continue;
                        for (int k = j+1; k < n; k++) {

                            for (int z = 0; z < 3; z++) {
                                auto c = bb[k][z];

                                if (c.second.second == a.second.second || c.second.second == b.second.second) continue;
                                ans = max(ans, 1LL * a.first + b.first + c.first);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
