class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        long long ans = LLONG_MIN;  // 使用 LLONG_MIN 而不是 LONG_LONG_MIN
        vector<tuple<int, int, int>> res;

        // 将所有的点放入向量中
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                res.push_back({i, j, board[i][j]});
            }
        }

        // 按照价值从大到小排序
        sort(res.begin(), res.end(), [](const tuple<int, int, int>& t1, const tuple<int, int, int>& t2) {
            return get<2>(t1) > get<2>(t2);
        });

        set<int> st1, st2;

        // 深度优先搜索（DFS）用于寻找不同行不同列的最大值组合
        function<void(int, long long, int)> dfs = [&](int index, long long cnt, int w) {
            if (w == 3) {
                ans = max(ans, cnt);
                return;
            }
            int c = 0;
            for (int i = index + 1; c < 9 && i < res.size(); ++i) {
                auto [x, y, z] = res[i];
                if (!st1.count(x) && !st2.count(y)) {
                    ++c;
                    st1.insert(x);
                    st2.insert(y);
                    dfs(i, cnt + z, w + 1);
                    st1.erase(x);
                    st2.erase(y);
                }
            }
        };

        // 处理前9个点，找出最大和
        for (int i = 0; i < 9 && i < res.size(); ++i) {
            auto [x1, y1, z1] = res[i];
            st1.insert(x1);
            st2.insert(y1);
            dfs(i, (long long)z1, 1);
            st1.erase(x1);
            st2.erase(y1);
        }

        return ans;
    }
};
