class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = (int) board.size();
        int m = (int) board[0].size();
        int N = n + m;
        int s = N++;
        int ss = N++;
        int t = N++;
        int tt = N++;

        const long long M = 1LL << 40;
        const int FL = 3;
        atcoder::mcf_graph<int, long long> mf(N);

        mf.add_edge(s, ss, FL, 0);
        mf.add_edge(tt, t, FL, 0);

        for (int i = 0; i < n; ++i) {
            mf.add_edge(ss, i, 1, 0);
        }
        for (int i = 0; i < m; ++i) {
            mf.add_edge(i + n, tt, 1, 0);
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                mf.add_edge(i, j + n, 1, M - board[i][j]);
            }
        }

        auto [fl, cost] = mf.flow(s, t);
        // cout << fl << " : " << cost << endl;
        long long res = M * 3 - cost;
        return res;
    }
};
