#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>> &board) {
        using ll = long long;
        int n = board.size(), m = board[0].size();
        vector<vector<pair<ll, int>>> row(n, vector<pair<ll, int>>(m));
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                row[i][j] = {board[i][j], j};
            }
            sort(row[i].begin(), row[i].end(), greater<>());
        }
        ll res = INT64_MIN;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (j == i) continue;
                for (int k = 0; k < n; k++) {
                    if (k == i || k == j) continue;
                    ll vi = row[i][0].first;
                    int ii = row[i][0].second;
                    if (vi + row[j][0].first + row[k][0].first <= res) continue;
                    for (int j_idx = 0; j_idx < 3; j_idx++) {
                        for (int k_idx = 0; k_idx < 3; k_idx++) {
                            int jj = row[j][j_idx].second;
                            int kk = row[k][k_idx].second;
                            if (ii != jj && ii != kk && jj != kk) {
                                res = max(res, vi + row[j][j_idx].first + row[k][k_idx].first);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
