class Solution {
private:
    const long long INF = 1e16;

public:
    long long maximumValueSum(vector<vector<int>>& a) {
        const int ROWS = a.size();
        const int COLS = a[0].size();
        const int K = 2 * (ROWS + COLS) + 3;

        using T3 = tuple<int, int, int>;
        priority_queue<T3, vector<T3>, greater<T3>> minHeap;
        for(int row = 0; row < ROWS; ++row){
            for(int col = 0; col < COLS; ++col){
                minHeap.push({a[row][col], row, col});
                if((int)minHeap.size() > K){
                    minHeap.pop();
                }
            }
        }

        const int M = minHeap.size();
        vector<T3> v(M);
        for(int i = 0; i < M; ++i){
            v[i] = minHeap.top();
            minHeap.pop();
        }

        long long res = -INF;
        vector<int> r(3);
        vector<int> c(3);
        for(int i = 0; i < M; ++i){
            for(int j = i + 1; j < M; ++j){
                r[0] = get<1>(v[i]);
                c[0] = get<2>(v[i]);
                r[1] = get<1>(v[j]);
                c[1] = get<2>(v[j]);
                long long x = get<0>(v[i]);
                long long y = get<0>(v[j]);

                if(r[0] == r[1] || c[0] == c[1]){
                    continue;
                }

                if(x + y + get<0>(v.back()) < res){
                    continue;
                }

                for(int k = M - 1; k >= j + 1; --k){
                    r[2] = get<1>(v[k]);
                    c[2] = get<2>(v[k]);

                    if(!(r[0] == r[2] || r[1] == r[2] ||
                         c[0] == c[2] || c[1] == c[2])){
                        long long z = get<0>(v[k]);
                        res = max(res, x + y + z);
                        break;
                    }
                }
            }
        }

        return res;
    }
};
