class Solution {
public:
    long long ans = LLONG_MIN;
    bool rec(vector<tuple<int, int, int>> const &vals, int i, int count, long long total, set<int> &rows, set<int> &cols, bool skip) {
        long long pos = total;
        for (int j = i; j < vals.size(); ++j) {
            auto [a, b, c] = vals[j];
            if (!rows.count(b) && !cols.count(c)) {
                // skip
                if (count == 1 && !skip) {
                    // cout << "skipping\n";
                    rec(vals, j+1, count, pos, rows, cols, 1);
                }
                //
                pos += a;
                // cout << "take " << a << ' ' << b << ' ' << c << '\n';
                // cout << pos << '\n';
                rows.insert(b); cols.insert(c);
                if (count == 2) {
                    ans = max(ans, pos);
                    // cout << "done\n";
                    pos -= a;
                    rows.erase(b); cols.erase(c);
                    return 1;
                }
                bool gotit = rec(vals, j+1, count+1, pos, rows, cols, 0);
                pos -= a;
                rows.erase(b); cols.erase(c);
                if (gotit)
                    return 1;
            }
        }
        return 0;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<tuple<int, int, int>> vals;
        vals.reserve(m * n);
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                vals.emplace_back(board[i][j], i, j);
            }
        }
        sort(vals.rbegin(), vals.rend());
        set<int> x, y;
        rec(vals, 0, 0, 0, x, y, 0); x.clear(); y.clear();
        rec(vals, 1, 0, 0, x, y, 0); x.clear(); y.clear();
        rec(vals, 2, 0, 0, x, y, 0); x.clear(); y.clear();
        rec(vals, 3, 0, 0, x, y, 0); x.clear(); y.clear();
        return ans;
    }
};
