class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        using ll = long long;
        int n = v.size() , m = v[0].size();
        vector<vector<int>> up(n , vector<int> (m , -2e9)) , down(n , vector<int> (m));
        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < m ; j++){
                up[i][j] = v[i][j] ;
                if(i)up[i][j] = max(up[i][j] , up[i-1][j]);

            }
        }
        for(int i = n-1;i>=0;i--){
            for(int j = 0 ; j < m ; j++){
                down[i][j] = v[i][j];
                if(i + 1 < n)down[i][j] = max(down[i][j] , down[i+1][j]);
            }
        }
        ll ans = -1e12;

        for(int i = 1 ; i < n-1 ;i++){
            set<pair<int,int>> a , b , c;
            for(int j = 0 ; j < m ; j++){
                a.insert({up[i-1][j] , j});
                b.insert({v[i][j],j});
                c.insert({down[i+1][j],j});
                if(a.size() > 3)a.erase(a.begin());
                if(b.size() > 3)b.erase(b.begin());
                if(c.size() > 3)c.erase(c.begin());
            }
            for(auto &[v1  , c1]:a){
                for(auto &[v2  , c2]:b){
                    if(c1 != c2){
                        for(auto &[v3  , c3]:c){
                            if(c3 != c1 and c3 != c2)ans = max<ll>(ans , 1ll*v1+v2+v3);
                        }
                    }
                }
            }
        }
        return ans;
    }
};
