class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long int out = -1e15;
        set<pair<int, pair<int, int>>> v1, v2;
        vector<pair<int, pair<int, int>>> v;
        int m = board.size();
        int n = board[0].size();
        for(int i = 0; i < m; i++)
        {
            vector<pair<int, pair<int, int>>> temp;
            for(int j = 0; j < n; j++)
                temp.push_back({board[i][j], {i, j}});
            sort(temp.begin(), temp.end());
            v1.insert(temp[n - 1]);
            v1.insert(temp[n - 2]);
            v1.insert(temp[n - 3]);
        }
        for(int i = 0; i < n; i++)
        {
            vector<pair<int, pair<int, int>>> temp;
            for(int j = 0; j < m; j++)
                temp.push_back({board[j][i], {j, i}});
            sort(temp.begin(), temp.end());
            v2.insert(temp[m - 1]);
            v2.insert(temp[m - 2]);
            v2.insert(temp[m - 3]);
        }
        for(auto i:v1)
        {
            bool flag = false;
            for(auto j:v2)
                if(i.first == j.first && i.second.first == j.second.first &&
                i.second.second == j.second.second)
                {
                    flag = true;
                    break;
                }
            if(flag)
                v.push_back(i);
        }
        for(auto i:v)
        {
            int r1 = i.second.first;
            int c1 = i.second.second;
            // cout<<i.first<<" "<<r1<<" "<<c1<<endl;
            for(auto j:v)
            {
                int r2 = j.second.first;
                int c2 = j.second.second;
                if(r1 != r2 && c1 != c2)
                {
                    for(auto k:v)
                    {
                        int r3 = k.second.first;
                        int c3 = k.second.second;
                        if(r1 != r3 && r2 != r3 && c1 != c3 && c2 != c3)
                        {
                            long long int tt = i.first*1LL + j.first + k.first*1LL;
                            out = max(out, tt);
                        }
                    }
                }
            }
        }
        return out;
    }
};
