class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        topNine = []
        for i in range(len(board)):
            bests = self.getTopThree(board[i])
            for best in bests:
                best.append(i)
            for best in bests:
                col = best[1]
                count = 0
                for top in topNine:
                    if top[1] == col:
                        count += 1
                        if count == 3 and top[0] < best[0]:
                            # if best[0] == 71:
                            #     print(top)
                            #     print(best)
                            #     print("here")
                            top[0] = best[0]
                            top[1] = best[1]
                            top[2] = best[2]
                            topNine.sort(reverse=True)
                            break
                if count < 3 and (len(topNine) < 9 or best[0] > topNine[-1][0]):
                    topNine.append(best)
                    topNine.sort(reverse=True)
                    topNine = topNine[:9]
                # print(best, topNine)
        total = len(topNine)
        best = None
        for i in range(total):
            for j in range(i + 1, total):
                for k in range(j + 1, total):
                    if (
                        topNine[i][2] != topNine[j][2]
                        and topNine[i][2] != topNine[k][2]
                        and topNine[j][2] != topNine[k][2]
                        and topNine[i][1] != topNine[j][1]
                        and topNine[i][1] != topNine[k][1]
                        and topNine[j][1] != topNine[k][1]
                    ):
                        val = topNine[i][0] + topNine[j][0] + topNine[k][0]
                        if best is None or val > best:
                            best = val

        return best

    def getTopThree(self, board: List[int]):  # val, col, row
        topThree = []
        for i in range(len(board)):
            topThree.append([board[i], i])
            topThree.sort(reverse=True)
            topThree = topThree[:3]
        return topThree
