const int N = 502, oo = 1e9 + 10;
class Solution {
public:
    long long ma[3][N];
    int pos[3][N];
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        for (int i = 0; i < n; i++) {
            ma[0][i] = ma[1][i] = ma[2][i] = -oo;
            for (int j = 0; j < m; j++) {
                for (int k = 0; k < 3; k++) {
                    if (ma[k][i] < a[i][j]) {
                        for (int f = 2; f > k; f--)
                            ma[f][i] = ma[f - 1][i], pos[f][i] = pos[f - 1][i];
                        ma[k][i] = a[i][j], pos[k][i] = j;
                        break;
                    }
                }
            }
        }

        long long ans = -3ll * oo;
        int i, j, k, p1, p2, p3;
        for (i = 0; i < n; i++) {
            for (j = i + 1; j < n; j++) {
                for (k = j + 1; k < n; k++) {
                    if (pos[0][i] != pos[0][j] && pos[0][k] != pos[0][j] && pos[0][k] != pos[0][i]) {
                        ans = max(ans, ma[0][i] + ma[0][j] + ma[0][k]);
                        continue;
                    }
                    for (p1 = 0; p1 < 3; p1++) {
                        for (p2 = 0; p2 < 3; p2++) {
                            if (pos[p1][i] == pos[p2][j]) continue;
                            for (p3 = 0; p3 < 3; p3++) {
                                if (pos[p3][k] != pos[p2][j] && pos[p3][k] != pos[p1][i]) {
                                    ans = max(ans, ma[p1][i] + ma[p2][j] + ma[p3][k]);
                                    break;
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
