class Solution {
public:
    void aux(const vector<tuple<long long, int, int>>& squares, int depth, bitset<500> row, bitset<500> col, long long &best, int first = 0, long long total = 0) 
    {
        if(depth == 3)
        {
            best = max(best, total);
            return;
        }
        if (first >= squares.size())
            return;
        auto [val, x, y] = squares[first];
        if(total + (3 - depth) * (-val) < best)
            return;

        int tries = 0;
        for(int i = first; i < squares.size(); i++)
        {
            auto [val, x, y] = squares[i];
            if(!row[x] && !col[y])
            {
                tries++;
                row[x] = col[y] = 1;
                aux(squares, depth + 1, row, col, best, i + 1, total - val);
                row[x] = col[y] = 0;
            }
            if(tries > 4)
                return;
        }
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        long long best = -3000000000;
        vector<tuple<long long, int, int>> squares;
        squares.reserve(board.size()*board[0].size());
        for(int i = 0; i < board.size(); i++)
            for(int j = 0; j < board[0].size(); j++)
                squares.push_back({-board[i][j], i, j});
        sort(begin(squares), end(squares));
        aux(squares, 0, {}, {}, best);
        return best;
    }
};
