class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size(),n=board[0].size();
        vector<vector<int>> del(m,vector<int>(n,0));
        vector<vector<pair<int,int>>> nboard;
        vector<int> rownum;

        for(int i=0;i<m;i++){
            priority_queue<pair<int,int>> pq;
            for(int j=0;j<n;j++){
                pq.push({board[i][j],j});
            }
            for(int j=0;j<3;j++){
                pq.pop();
            }
            while(!pq.empty()){
                pair<int,int> x=pq.top();
                pq.pop();
                del[i][x.second]=1;
            }
        }
        for(int j=0;j<n;j++){
            priority_queue<pair<int,int>> pq;
            for(int i=0;i<m;i++){
                pq.push({board[i][j],i});
            }
            for(int j=0;j<3;j++){
                pq.pop();
            }
            while(!pq.empty()){
                pair<int,int> x=pq.top();
                pq.pop();
                del[x.second][j]=1;
            }
        }
        // for(int i=0;i<m;i++){
        //     for(int j=0;j<n;j++){
        //         cout<<del[i][j]<<" ";
        //     }cout<<endl;
        // }
        for(int i=0;i<m;i++){
            vector<pair<int,int>> v;
            for(int j=0;j<n;j++){
                if(!del[i][j]){
                    v.push_back({board[i][j],j});
                }
            }
            if(!v.empty()){
                rownum.push_back(i);
                nboard.push_back(v);
            }
        }
        m=rownum.size();
        long long ans=LONG_LONG_MIN;
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int l=j+1;l<m;l++){
                    for(int i1=0;i1<nboard[i].size();i1++){
                        for(int i2=0;i2<nboard[j].size();i2++){
                            for(int i3=0;i3<nboard[l].size();i3++){
                                if(nboard[i][i1].second==nboard[j][i2].second||nboard[i][i1].second==nboard[l][i3].second||nboard[j][i2].second==nboard[l][i3].second){
                                    continue;        
                                }
                                ans=max(ans,1ll*nboard[i][i1].first+1ll*nboard[j][i2].first+1ll*nboard[l][i3].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
