using i64 = long long;

const i64 INF = 4000000000LL;

int a[505][3];

class Solution {
public:
    i64 maximumValueSum(vector<vector<int>>& g) {
        // cout << 500 * 499 * 498 / 6 * 9 << '\n';
        int n = g.size(), m = g[0].size();
        auto gao = [&](int id) {
            a[id][0] = -1, a[id][1] = -1, a[id][2] = -1;
            for (int j = 0; j < m; j++) {
                int x = g[id][j];
                if (a[id][0] == -1 || x > g[id][a[id][0]]) a[id][2] = a[id][1], a[id][1] = a[id][0], a[id][0] = j;
                else if (a[id][1] == -1 || x > g[id][a[id][1]]) a[id][2] = a[id][1], a[id][1] = j;
                else if (a[id][2] == -1 || x > g[id][a[id][2]]) a[id][2] = j;
            }
        };
        for (int i = 0; i < n; i++) gao(i);
        i64 ans = -INF;
        for (int i = 0; i < n; i++) for (int j = i + 1; j < n; j++) for (int k = j + 1; k < n; k++) {
            bool ok = false;
            for (int x = 0; x < 3; x++) {
                for (int y = 0; y < 3; y++) {
                    if (a[i][x] == a[j][y]) continue;
                    for (int z = 0; z < 3; z++) {
                        if (a[i][x] == a[k][z] || a[j][y] == a[k][z]) continue;
                        i64 tmp = (i64) g[i][a[i][x]] + g[j][a[j][y]] + g[k][a[k][z]];
                        ans = max(ans, tmp);
                        if (x == 0 && y == 0 && z == 0) {
                            ok = true;
                        }
                        break;
                    }
                    if (ok) break;
                }
                if (ok) break;
            }
        }
        return ans;
    }
};
