typedef long long ll;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll inf=LLONG_MIN/2;
        ll ans=inf;
        vector<tuple<ll,int,int>>v;
        int n=board.size(),m=board[0].size();
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v.emplace_back(board[i][j],i,j);
            }
        }
        sort(v.begin(),v.end());
        int si=v.size(),i1,j1,i2,j2,i3,j3;
        ll t1=0,t2=0,t3=0;
        for(int i=v.size()-1;i>=0;i--){
            if(v.size()-i>=100)break;
            tie(t1,i1,j1)=v[i];
            for(int j=i-1;j>=0;j--){
                if(i-j>=500)break;
                tie(t2,i2,j2)=v[j];
                if(i2==i1||j2==j1)continue;                
                for(int k=j-1;k>=0;k--){
                    if(j-k>=500)break;
                    tie(t3,i3,j3)=v[k];
                    if(i3==i1||i3==i2||j3==j1||j3==j2)continue;
                    ans=max(ans,t1+t2+t3);
                }
            }
        }
        return ans;
    }
};
