class Solution {
    public long maximumValueSum(int[][] board) {
        int n = board.length;
        int m = board[0].length;
        int[][] rmax = new int[n][3];
        int[][] ridx = new int[n][3];
        for(int i = 0;i < n;i++){
            Arrays.fill(rmax[i] , Integer.MIN_VALUE);
            for(int j = 0;j < m;j++){
                if(board[i][j] > rmax[i][0]){
                    rmax[i][2] = rmax[i][1];
                    rmax[i][1] = rmax[i][0];
                    rmax[i][0] = board[i][j];

                    ridx[i][2] = ridx[i][1];
                    ridx[i][1] = ridx[i][0];
                    ridx[i][0] = j;
                }else if(board[i][j] > rmax[i][1]){
                    rmax[i][2] = rmax[i][1];
                    rmax[i][1] = board[i][j];

                    ridx[i][2] = ridx[i][1];
                    ridx[i][1] = j;
                }else if(board[i][j] > rmax[i][2]){
                    rmax[i][2] = board[i][j];
                    ridx[i][2] = j;
                }
            }
        } 

        int[][] arr = new int[n * 3][3];
        int idx = 0;
        for(int i = 0;i < n;i++){
            for(int j = 0;j < 3;j++){
                arr[idx][0] = rmax[i][j];
                arr[idx][1] = i;
                arr[idx][2] = ridx[i][j];
                idx += 1;
            }
        }



        Arrays.sort(arr, (a,b) -> {return b[0] - a[0];});

        // for(int i = 0;i < 3 * n;i++){
        //     System.out.println(arr[i][0] +" _ " + arr[i][1] + " _ " + arr[i][2] );
        // }

        int x1 = -1,x2 = -1,x3 = -1,x4 = -1;
        long max =  Long.MIN_VALUE / 2;
        for(int i = 0;i < 3 * n ;i++){
            long t = (long)arr[i][0];
            x1 = arr[i][1];
            x2 = arr[i][2];
            for(int j = i + 1;j < 3 * n;j++){
                if(arr[j][1] == x1 || arr[j][2] == x2){
                    continue;
                }

                if(t + arr[j][0] * 2 <= max){
                    break;
                }

                t += arr[j][0];
                x3 = arr[j][1];
                x4 = arr[j][2];
                for(int k = j + 1;k < 3 * n;k++){
                    if(arr[k][1] == x1 || arr[k][2] == x2 || arr[k][1] == x3 || arr[k][2] == x4){
                        continue;
                    }
                    if(arr[k][0] + t > max){
                        max = arr[k][0] + t;
                    }else{
                        break;
                    }
                }

                t -= arr[j][0];
            }

        }

        return max;

    }

}
