class Solution {
public:
    #define loop(i,a,b) for(int i=a;i<b;i++)
    #define pool(i,a,b) for(int i=a;i>=b;i--)
    #define pb push_back
    #define um unordered_map
    #define all(a) a.begin(), a.end()
    #define rall(a) a.rbegin(), a.rend()
    #define sz(a) a.size()
    #define mp make_pair
    typedef long long ll;
    typedef vector<int> vi;
    typedef pair<int,int> pi;

    long long maximumValueSum(vector<vector<int>>& grid) {
        ll max_sum = LLONG_MIN;
        int m = sz(grid), n = sz(grid[0]);

        vector<vector<pi>> three(m);
        loop(i, 0, m) {
            vector<pi> vec;
            loop(j, 0, n) {
                vec.pb(mp(grid[i][j], j));
            }
            sort(rall(vec));
            loop(k, 0, min(3, n)) {
                three[i].pb(vec[k]);
            }
        }

        loop(i, 0, m) {
            loop(j, i + 1, m) {
                for (auto &[val1, col1] : three[i]) {
                    for (auto &[val2, col2] : three[j]) {
                        if (col1 == col2) continue;

                        int best = INT_MIN;
                        loop(x, j, m) {
                            if (x == i || x == j) continue;
                            for (auto &[val3, col3] : three[x]) {
                                if (col3 != col1 && col3 != col2) {
                                    best = max(best, val3);
                                    break; 
                                }
                            }
                        }

                        if (best != INT_MIN) {
                            ll sum = 1ll * val1 + 1ll * val2 + 1ll * best;
                            max_sum = max(max_sum, sum);
                        }
                    }
                }
            }
        }

        return max_sum;
    }
};
