class Solution {
public:
#pragma GCC optimize(2)
    long long maximumValueSum(vector<vector<int>>& board) {
        int r=board.size(),c=board[0].size();  
        using PII = pair<int,int>;   
        vector<vector<PII>>maxx(r,vector<PII>(c));
        for(int i=0;i<r;++i)
        {
            for(int j=0;j<c;++j)
            {
                maxx[i][j]={board[i][j],j};
            }
            sort(maxx[i].begin(), maxx[i].end(), [&](const PII &a, const PII &b) {
                return a.first > b.first;
            });
            while(maxx[i].size()>3)maxx[i].pop_back();
        }
        /*
        sort(maxx.begin(),maxx.end(),[&](const vector<PII>&a, const vector<PII>&b){
            return (long long)a[0].first+a[1].first+a[2].first>(long long)b[1].first+b[1].first>b[2].first;
        });
        */
        long long ans=-3e9;
        for(int i=0;i<r;++i)
        {
            for(int j=i+1;j<r;++j)
            {
                for(int k=j+1;k<r;++k)
                {
                    if((long long)maxx[i][0].first+maxx[j][0].first+maxx[k][0].first<=ans)continue;
                    for(int p1=0;p1<3;++p1)
                    {
                        for(int p2=0;p2<3;++p2)
                        {
                            for(int p3=0;p3<3;++p3)
                            {
                                PII a=maxx[i][p1],b=maxx[j][p2],c=maxx[k][p3];

                                if(a.second!=b.second&&c.second!=b.second&&a.second!=c.second)
                                {
                                    /*
                                    cout<<"a:"<<i<<" "<<a.second<<" "<<a.first<<endl;
                                cout<<"b:"<<j<<" "<<b.second<<" "<<b.first<<endl;
                                cout<<"c:"<<k<<" "<<c.second<<" "<<c.first<<endl;
                                cout<<a.first+b.first+c.first<<endl;
                                */
                                    ans=max(ans,(long long)a.first+b.first+c.first);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
