#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<ll>>Maxi(m, vector<ll>(3));
        for(int i = 0; i < m; i++){
            vector<pair<int,int>>v;
            for(int j = 0; j < n; j++){
                v.push_back({board[i][j], j});
            }
            sort(rbegin(v), rend(v));
            for(int j = 0; j < 3; j++){
                Maxi[i][j] = v[j].second;
            }
        }

        vector<vector<ll>>bottomMax(m, vector<ll>(n));
        for(int i = m - 1; i >= 0; i--){
            for(int j = 0; j < n; j++){
                if(i == m - 1) bottomMax[i][j] = board[i][j];
                else bottomMax[i][j] = max(board[i][j]*1LL, bottomMax[i + 1][j]);
            }
        }
        vector<vector<pair<ll,ll>>>rowMax(m, vector<pair<ll,ll>>(3));
        for(int i = 0; i < m; i++){
            vector<pair<ll, ll>>v;
            for(int j = 0; j < n; j++){
                v.push_back({bottomMax[i][j], j});
            }
            sort(rbegin(v), rend(v));
            for(int k = 0; k < 3; k++){
                rowMax[i][k] = {v[k].second, v[k].first};
            }
        }
        ll ans = LLONG_MIN;
        for(int r1 = 0; r1 < m; r1++){
            for(int r2 = r1 + 1; r2 < m; r2++){
                if(r2 == m - 1) break;
                    for(int i = 0; i < 3; i++){
                        for(int j = 0; j < 3; j++){
                            for(int k = 0; k < 3; k++){
                                if(Maxi[r1][i] != Maxi[r2][j] && Maxi[r1][i] != rowMax[r2 + 1][k].first && rowMax[r2 + 1][k].first != Maxi[r2][j])
                                ans = max(ans, board[r1][Maxi[r1][i]]*1LL + board[r2][Maxi[r2][j]]*1LL + rowMax[r2 + 1][k].second);
                            }
                        }
                    }

            }
        }
        return ans;
    }
};
