inline ll mul(ll x,ll y){
    x%=mod;y%=mod;
    x+=mod;y+=mod;
    return x*y %mod;
}

inline ll add(ll x,ll y){
    x%=mod;y%=mod;
    x+=mod;y+=mod;
    return (x+y) %mod;
}

inline ll power(ll x,ll y){
    x%=mod;
    ll res=1;
    while(y){
        if(y&1)
            res=res*x %mod;
        x=x*x %mod;
        y/=2;
    }
    return res;
}

class Solution {
public:
    ll n,m;
    long long maximumValueSum(vector<vector<int>>& nums) {
        n=len(nums);
        m=len(nums[0]);
        ll ans=-1e18;
        vector<vector<pii>> a(n);
        rep(i,n){
            set<pii> st;
            rep(j,m){
                st.insert({nums[i][j],j});
                if(len(st)>3)st.erase(st.begin());
            }
            for(auto x:st)a[i].pb(x);
        }


        rep(i,n){
            rep(ii,3){
                multiset<pii> st;
                for(ll j=i+1;j<n;j++){
                    rep(jj,3){st.insert(a[j][jj]);}
                }
                // cout<<i<<" "<<len(st)<<endl;
                for(ll j=i+1;j<n;j++){
                    rep(jj,3)st.erase(st.find(a[j][jj]));
                    rep(jj,3){
                        if(a[i][ii].S==a[j][jj].S)continue;
                        auto it=st.end();
                        // cout<<len(st)<<endl;;
                        while(it!=st.begin()){
                            --it;
                            if(it->S==a[i][ii].S || it->S==a[j][jj].S)continue;
                            // cout<<i<<" ";
                            // cout<<j<<" ";
                            // cout<<a[i][ii].F<<" ";
                            // cout<<a[j][jj].F<<": ";
                            // cout<<it->F<<" \n";
                            ans=max(ans,a[i][ii].F+a[j][jj].F+it->F);
                            // cout<<"hi";
                            break;
                        }
                    }
                }
            }
        }
        if(ans==-1e18)return -100;
        return ans;
    }
};
